import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import { useEffect, useState } from "react";
import {
  featuredPortfolio,
  webPortfolio,
  mobilePortfolio,
  contentPortfolio,
} from "../../data";

export default function Portfolio() {
  const [selected, setSelected] = useState("featured");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "featured",
      title: "Destacado",
    },
    {
      id: "web",
      title: "Aplicación Web",
    },
    {
      id: "mobile",
      title: "Aplicación Móvil",
    },
    {
      id: "desktop",
      title: "Aplicación de Escritorio",
    },
  ];
  useEffect(() => {
    switch (selected) {
      case "featured":
        setData(featuredPortfolio);
        break;
      case "web":
        setData(webPortfolio);
        break;
      case "movile":
        setData(mobilePortfolio);
        break;
      case "desktop":
        setData(desktopPortfolio);
        break;
        default:
          setData(featuredPortfolio);
    }
  }, [selected]);
  return (
    <div className="portfolio" id="portfolio">
      <h1>Portafolio</h1>
      <ul>
        {list.map((item) => (
          <PortfolioList
            title={item.title}
            active={selected == item.id}
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
      </div>
    </div>
  );
}
