import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import { useEffect, useState } from "react";
import {
  featurePortfolio,
  webPortfolio,
  mobilePortfolio,
  contentPortfolio,
} from "../../data";

export default function Portfolio() {
  const [selected, setSelected] = useState("featured");
  const list = [
    {
      id: "featured",
      title: "Destacado",
    },
    {
      id: "web",
      title: "Aplicación Web",
    },
    {
      id: "mobile",
      title: "Aplicación Móvil",
    },
    {
      id: "desktop",
      title: "Aplicación de Escritorio",
    },
  ];
  useEffect
  return (
    <div className="portfolio" id="portfolio">
      <h1>Portafolio</h1>
      <ul>
        {list.map((item) => (
          <PortfolioList
            title={item.title}
            active={selected == item.id}
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
        <div className="item">
          <img
            src="https://www.480interactive.com/blog/wp-content/uploads/2017/07/app-versus-web-movil-695x463@2x.jpg"
            alt=""
          />
          <h3>WhatsApp Clone</h3>
        </div>
      </div>
    </div>
  );
}
